import java.util.ArrayList;import java.util.List;public class n {public static void main(String[]args){List<int[]>testCase=new ArrayList<int[]>();testCase.add(new int[]{1,4,2,3,45});testCase.add(new int[]{1,4,2,3,5});testCase.add(new int[]{1,4,13,3,1});testCase.add(new int[]{1,4,2,1231,45});List<Integer>expectedResult=new ArrayList<Integer>();expectedResult.add(45);expectedResult.add(5);expectedResult.add(123);expectedResult.add(123);for(int i=0;i<testCase.size();i++){int[]input=testCase.get(i);int actualResult=Answer(input);int expected=expectedResult.get(i);System.out.println("Expected Result: "+expected);System.out.println("Actual Result: "+actualResult);System.out.println("Test Result: "+(actualResult==expected?"Passed":"Failed"));System.out.println();}}public static int Answer(int[]input){int max=input[0];for(int i=1;i<input.length;i++){if(input[i]>max){max=input[i];}}return max;}}